module.exports = function (ms) {
    var second = ms / 1000;
    if (!second) {
        return 0;
    }
    var time = '';
    if (second >= 24 * 3600) {
        time += parseInt(second / (24 * 3600)) + '天';
        second %= 24 * 3600;
    }
    if (second >= 3600) {
        time += parseInt(second / 3600) + '小时';
        second %= 3600;
    }
    if (second >= 60) {
        time += parseInt(second / 60) + '分钟';
        second %= 60;
    }
    if (second > 0) {
        time += second + '秒';
    }
    return time;
}